import React, { useEffect, useState } from "react";
// import Data from "./Data";
import { Link } from "react-router-dom";
import axios from "axios";

const Products = () => {
  const [getProducts, setProductsData] = useState([]);
  // const [getcategory, setcategory] = useState([]);
  const [getcategory, setCategory] = useState();

  useEffect(() => {
    getAllProducts();
  }, []);

  useEffect(() => {
    getcategoryData();
  }, []);
  const getAllProducts = () => {
    const url = "https://aveosoft-react-assignment.herokuapp.com/products";
    axios.get(`${url}`).then((res) => {
      console.log(res["data"]);
      setProductsData(res["data"]);
      // setAllProductsList(res["data"]);
    });
  };

  const getcategoryData = () => {
    const url = "https://aveosoft-react-assignment.herokuapp.com/categories";
    axios.get(`${url}`).then((res) => {
      console.log(res["data"]);
      setCategory(res["data"]);
      // setAllProductsList(res["data"]);
    });
  };

  const byCategory = (getcategory, category) => {
    if (category) {
      return getcategory.name === category;
    } else return getProducts;
  };

  // const filteredList = (getProducts, category) => {
  //   return getProducts.filter((getProducts) =>
  //     byCategory(getProducts, category)
  //   );
  //   // .filter(user => bySearch(user, search));
  // };

  const products = getProducts.map((product) => {
    return (
      <div className="col-lg-4 col-md-2 col-12 mt-3" key={product.id}>
        <div className="card" id={product.id}>
          <div className="card-body">
            <h3 className="p-title">
              <Link to={`/products/${product.id}`}>{product.name}</Link>
            </h3>
            <p className="card-text">
              <small className="text-muted">{product.category}</small>
            </p>
            <p className="card-text">{product.price}</p>
            <p className="card-text">{product.price}</p>
            <p className="card-text">{product.description}</p>
          </div>
        </div>
      </div>
    );
  });

  return (
    <>
      <div className="container">
        <div className="row">
          <h1>Products Page</h1>
          <select
            class="form-select"
            aria-label="Default select example"
            onChange={(e) => setCategory(e.target.value)}
          >
            <option value="">All</option>
            {getcategory.map((cat) => {
              return <option value={cat.name}>{cat.name}</option>;
            })}
          </select>
          {/* {filteredList(getProducts, category).map((user) => (
            <div key={user.id}>
              {user.name} : {user.category}
            </div>
          ))} */}
          {products}
        </div>
      </div>
    </>
  );
};

export default Products;
