import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

const Products = () => {
  const [getProducts, setProductsData] = useState([]);
  // const [getcategory, setCategory] = useState([]);
  const [category, setCategory] = useState("all");

  useEffect(() => {
    getAllProducts();
  }, []);

  useEffect(() => {
    getcategoryData();
  }, []);

  useEffect(() => {
    let filteredProducts = getProducts;

    if (category !== "all") {
      filteredProducts = filteredProducts.filter(
        (product) => product.category === category
      );
    }

    setProductsData(filteredProducts);
    //variable being listened for change
  }, [category, min, max]);

  const getAllProducts = () => {
    const url = "https://aveosoft-react-assignment.herokuapp.com/products";
    axios.get(`${url}`).then((res) => {
      // console.log(res["data"]);
      setProductsData(res["data"]);
    });
  };

  const getcategoryData = () => {
    const url = "https://aveosoft-react-assignment.herokuapp.com/categories";
    axios.get(`${url}`).then((res) => {
      console.log(res["data"]);
      setCategory(res["data"]);
    });
  };
  console.log(getcategory[0]);
  // [0,1] = [getcategory[0].id,getcategory[1].id]

  const products = getProducts.map((product) => {
    return (
      <div className="col-lg-4 col-md-2 col-12 mt-3" key={product.id}>
        <div className="card" id={product.id}>
          <div className="card-body">
            <h3 className="p-title">
              <Link to={`/products/${product.id}`}>{product.name}</Link>
            </h3>
            <p className="card-text">
              <small className="text-muted">{product.category}</small>
            </p>
            <p className="card-text">{product.price}</p>
            <p className="card-text">{product.price}</p>
            <p className="card-text">{product.description}</p>
          </div>
        </div>
      </div>
    );
  });

  const selectFilter = (e) => {
    console.log(e.target.options[e.target.selectedIndex].id);

    if (e.target.value === "All") {
      getAllProducts();
      console.log("all");
    } else {
      let newData = [];
      console.log(
        getProducts.filter(
          (res) =>
            e.target.options[e.target.selectedIndex].id === res.categoryId
        )
      );
      newData = getProducts.filter(
        (res) => e.target.options[e.target.selectedIndex].id === res.categoryId
      );
      console.log(newData);
      setProductsData(newData);
    }
  };

  return (
    <>
      <div className="container">
        <div className="row">
          <h1>Products Page</h1>
          <select
            className="form-select"
            aria-label="Default select example"
            value="{getcategory}"
            onChange={(e) => handleFilterChange(e, "category")}
          >
            <option value="All" id="">
              All
            </option>
            <option value="Laptop" id="0">
              Laptop
            </option>
            <option value="Mobile" id="1">
              Mobile
            </option>
          </select>
          {products}
        </div>
      </div>
    </>
  );
};

export default Products;
